INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12574,'http:\/\/cs10379.userapi.com\/u12766380\/-14\/m_c4b60f07.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/x_f3c3514c.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/s_c092c11e.jpg','Лучок шинкуем. Яйца отвариваем до крутых.','');
