INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12574,'http:\/\/cs10379.userapi.com\/u12766380\/-14\/m_3e2970a9.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/x_9a8e95ed.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/s_f2e30010.jpg','И напоследок слой рыбы.','');
