INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12574,'http:\/\/cs10379.userapi.com\/u12766380\/-14\/m_c9aee9f3.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/x_946e3a60.jpg','http:\/\/cs10379.userapi.com\/u12766380\/-14\/s_a764528f.jpg','Ставим в духовку на 180°C и на 40 минут. Достаем и оставляем отдохнуть его еще на 10 минут.','');
