INSERT INTO cooksteps(cookbook_id, src, src_big, src_small, text, text_en) VALUES(12728,'http:\/\/cs410719.userapi.com\/v410719380\/705b\/bQ3Trc4qNYM.jpg','http:\/\/cs410719.userapi.com\/v410719380\/705c\/BZWr5Heobfk.jpg','http:\/\/cs410719.userapi.com\/v410719380\/705a\/AtImgJHaFuY.jpg','Каждый блинчик смажьте столовой ложкой любимого джема.','');
